import React, { useState, useEffect } from 'react';//импортируем методы useState и useEffect


function Express(props) {//создан компонент который получит данные из экспресса
  const [data, setData] = useState([]);// создаем состояние data где хранятся полученные данные, setData для обновления состояния data

  useEffect(() => {
    // Отправляем GET запрос к вашему серверу Express
    fetch('http://localhost:3001/product')
      .then(response => response.json())
      .then(data => {
        // Обновляем состояние данных в компоненте React
        setData(data);
        console.log(data); // Выводим данные в консоль
      })
      .catch(error => {
        console.error('Ошибка получения данных из сервера:', error);
      });
  }, []);
  
 return (<div>
  <h1>Данные из Express:</h1>
  <ul>
    {/* Отображаем полученные данные */}
    {data.map(item => (
      <li key={item.id}>{item.img}{item.titleProduct}{item.overview}{item.productAvability}</li>
    ))}
  </ul>
</div>)
}

export default Express;
