import React, { useState, useEffect } from 'react';
import pageAdminStyles from './PageAdmin.module.css';
import axios from 'axios';
import { Input, Button, Table } from 'reactstrap';

const PageAdmin = () => {
    const [titleProduct, setTitleProduct] = useState([]);
    const [overview, setOverview] = useState('');
    const [price, setPrice] = useState('');
    const [avability, setAvability] = useState('');
    const [image, setImage] = useState(null);
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetchProducts();
    }, [])

    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:3001/');
            setProducts(response.data);
        } catch (error) {
            console.error('Ошибка при получении списка продуктов:', error);
        }
    };

    const handleProductTitle = (event) => {
        setTitleProduct(event.target.value);
    };

    const handleOverview = (event) => {
        setOverview(event.target.value);
    };
    const handlePrice = (event) => {
        setPrice(event.target.value);
    };
    const handleAvability = (event) => {
        setAvability(event.target.value);
    };
    const handleImage = (event) => {
        setImage(event.target.files[0]);
    };



    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        formData.append('titleProduct', titleProduct);
        formData.append('overview', overview);
        formData.append('price', price);
        formData.append('avability', avability);
        formData.append('image', image);

        try {
            const response = await axios.post('http://localhost:3001/admin', formData);

            if (response.status !== 200) {
                throw new Error('Ошибка при отправке запроса');
            }

            console.log('Успешно добавлено:', response.data);

            // После добавления продукта обновляем список
            fetchProducts();
        } catch (error) {
            console.error('Ошибка при отправке данных на сервер:', error);
        }
    };
    const handleDelete = async (productId) => {
        try {
            await axios.delete(`http://localhost:3001/admin/${productId}`);
            alert('Продукт успешно удален');
            // После удаления продукта обновляем список
            fetchProducts();
        } catch (error) {
            console.error('Ошибка при удалении продукта:', error);
            alert('Произошла ошибка при удалении продукта');
        }
    };

    const handleReplace = async (productId) => {
        const productToReplace = products.find(product => product.id === productId);

        // Отправляем запрос на замену продукта
        try {
            const response = await axios.put(`http://localhost:3001/admin/${productId}`, {
                titleProduct: productToReplace.titleProduct,
                overview: productToReplace.overview,
                price: productToReplace.price,
                availability: productToReplace.availability,
                image: productToReplace.image
            });

            if (response.status === 200) {
                alert('Продукт успешно заменен');
                // После замены продукта обновляем список
                fetchProducts();
            } else {
                throw new Error('Ошибка при замене продукта');
            }
        } catch (error) {
            console.error('Ошибка при замене продукта:', error);
            alert('Произошла ошибка при замене продукта');
        }
    };
    return (
        <div>
            <div className={pageAdminStyles.container}>
                <h1>Страница администратора магазина</h1>
                <h3>Добавление товара на сервис</h3>
                <form onSubmit={handleSubmit}>
                    <Input
                        type="text"
                        placeholder="Введите название продукта"
                        value={titleProduct}
                        onChange={handleProductTitle}
                    /><br/>
                    <Input
                        type="text"
                        placeholder="Введите описание продукта"
                        value={overview}
                        onChange={handleOverview}
                    /><br/>
                    <Input
                        type="text"
                        placeholder="Введите цену продукта"
                        value={price}
                        onChange={handlePrice}
                    /><br/>
                    <Input
                        type="text"
                        placeholder="Введите количество"
                        value={avability}
                        onChange={handleAvability}
                    /><br/>
                    <Input
                        type="file"
                        name="image"
                        onChange={handleImage}
                    /><br/>
                <Button color="secondary" type="submit">
                    Добавить продукт
                </Button >
                
                </form>
                <h3>Список продуктов</h3>
                    <div>
                        <Table bordered hover>
                            <thead>
                                <tr>
                                    <th>Название</th>
                                    <th>Описание</th>
                                    <th>Цена</th>
                                    <th>Наличие</th>
                                    <th>Управление</th> {/* Добавим столбец для кнопки удаления */}
                                </tr>
                            </thead>
                            <tbody>
                                {products.map(product => (
                                    <tr key={product.id}>
                                        <td>{product.titleProduct}</td>
                                        <td>{product.overview}</td>
                                        <td>{product.price}</td>
                                        <td>{product.avability}</td>
                                        <td>
                                            {/* Кнопка для удаления продукта */}
                                            <Button color="primary" onClick={() => handleDelete(product.id)}>Удалить</Button>
                                            <Button color="primary" onClick={() => handleReplace(product.id)}>Заменить</Button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </Table>
                    </div>
            </div>
        </div>
    );
};

export default PageAdmin;
