import React, { useState } from 'react';
import pageAdmin from './PageAdmin.module.css';

const PageAdmin = () => {
    const [titleProduct, setProductTitle] = useState('');//создается состояние productId хранит текущее значение поля ввода
    
    
    const handleProductTitleChange = (event) => {// функция предназначенная для обновления состояния productId в React при изменении значения поля ввода
        setProductTitle(event.target.value);
    };


    const handleSubmit = async (event) => {//вызывается при отправке формы
        event.preventDefault();
        const formData = new FormData();// данные формы для отправки на сервер
        formData.append('titleProduct', titleProduct);//значение хранящееся в состоянии
        console.log('Отправляем данные на сервер:', formData);

        try {
            const response = await fetch('http://localhost:3001/admin', {
                method: 'POST',
                body: formData
            });
            const data = await response.json();
            console.log('Ответ от сервера:', data);
        } catch (error) {
            console.error('Ошибка при отправке данных на сервер:', error);
        }

    };

return (
    <div>
        <div className={pageAdmin.container}>
            <h1>Страница администратора магазина</h1>
            <h3>Редактирование карточки товара</h3>
            <form onSubmit={handleSubmit}>
                <input 
                    type="text" 
                    placeholder="Введите название продукта" 
                    value={titleProduct} 
                    onChange={handleProductTitleChange} 
                /><br/>
                <button type="submit">Добавить продукт</button>
            </form> 
        </div>
    </div>

    );
    
};

export default PageAdmin;



