import React, { useState } from 'react';
import pageAdmin from './PageAdmin.module.css';


const PageAdmin = () => {
    const [productId, setProductId] = useState('');//состояние productId
    const [productImage, setProductImage] = useState('');//состояние productImage
    const [productTitle, setProductTitle] = useState('');//состояние productTitle
    const [productImages, setProductImages] = useState('');//состояние productImages
    
    // Обработчик изменения id значения в поле ввода
    const handleProductIdChange = (event) => {
        setProductId(event.target.value);//функция для обновления значения id
    };
     // Обработчик изменения пути изображения в поле ввода
     const handleProductImageChange = (event) => {
        setProductImage(event.target.value);//функция для обновления значения названия
    };
    // Обработчик изменения названия в поле ввода
    const handleProductTitleChange = (event) => {
        setProductTitle(event.target.value);//функция для обновления значения названия
    };
     // Обработчик изменения пути изображения в поле ввода
     const handleProductImagesChange = (event) => {
        setProductImages(event.target.value);//функция для обновления значения названия
    };

    // Обработчик отправки формы
    const handleSubmit = (event) => {
        event.preventDefault();
        console.log('Id продукта:', productId);
        console.log('Путь изображения продукта:', productImage);
        console.log('Название продукта:', productTitle);
        console.log('Добавьте картинку:', productImages);
        sendProductData(productId, productImage, productTitle, productImages);// данные для отправки на сервер
        
    };
    const sendProductData = async (productId, productImage, productTitle, productImages) => {
        try {
          const response = await fetch('http://localhost:3001/admin', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({ id: productId, image: productImage, title: productTitle, images: productImages })
          });
          const data = await response.json();
          console.log('Ответ от сервера:', data);
        } catch (error) {
          console.error('Ошибка при отправке данных на сервер:', error);
        }
      };

    return (
        <div className={pageAdmin.container}>
            <h1>Страница администратора магазина</h1>
            <h3>Редактирование карточки товара</h3>
            <form onSubmit={handleSubmit}>
                {/* Поле ввода Id продукта */}
                <input 
                    type="text" 
                    placeholder="Введите Id продукта" 
                    value={productId} 
                    onChange={handleProductIdChange} 
                /><br/>
                {/* поле добавления изображения */}
                <input 
                    type="file" 
                    placeholder="Добавьте картинку" 
                    value={productImages} 
                    onChange={handleProductImagesChange} 
                /><br/>
                {/* Поле ввода для пути изображения продукта */}
                <input 
                    type="text" 
                    placeholder="Введите название изображения" 
                    value={productImage} 
                    onChange={handleProductImageChange} 
                /><br/>
                {/* Поле ввода для цены продукта */}
                <input 
                    type="text" 
                    placeholder="Введите название продукта" 
                    value={productTitle} 
                    onChange={handleProductTitleChange} 
                /><br/>{
                /* Кнопка отправки формы */}
                <button type="submit">Добавить продукт</button>
            </form>
            
        </div>
    );
}

export default PageAdmin;
