import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Input, Button, Table } from 'reactstrap';

const PageAdmin = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetchProducts();
    }, [])

    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:3001/');
            setProducts(response.data);
        } catch (error) {
            console.error('Ошибка при получении списка продуктов:', error);
        }
    };

    const handlePut = async (productId, newData) => {
        try {
            await axios.put(`http://localhost:3001/admin/${productId}`, newData);
            alert('Продукт успешно обновлен');
            fetchProducts();
        } catch (error) {
            console.error('Ошибка при обновлении продукта:', error);
            alert('Произошла ошибка при обновлении продукта');
        }
    };

    return (
        <div>
            <h3>Список продуктов</h3>
            <Table bordered hover>
                <thead>
                    <tr>
                        <th>Название</th>
                        <th>Описание</th>
                        <th>Цена</th>
                        <th>Наличие</th>
                        <th>Управление</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map(product => (
                        <tr key={product.id}>
                            <td>{product.titleProduct}</td>
                            <td>{product.overview}</td>
                            <td>{product.price}</td>
                            <td>{product.avability}</td>
                            <td>
                                <Button color="primary" onClick={() => handlePut(product.id, { titleProduct: 'Новое название' })}>Заменить</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </div>
    );
};

export default PageAdmin;
