import React, { useState } from 'react';
import pageAdmin from './PageAdmin.module.css';


const PageAdmin = () => {
    const [productId, setProductId] = useState('');
    const [productImage, setProductImage] = useState('');
    const [productTitle, setProductTitle] = useState('');
    const [productImages, setProductImages] = useState('');

    const handleProductIdChange = (event) => {
        setProductId(event.target.value);
    };

    const handleProductImageChange = (event) => {
        const file = event.target.files[0]; // Получаем первый выбранный файл
        setProductImage(file);
    };

    const handleProductTitleChange = (event) => {
        setProductTitle(event.target.value);
    };
// Обработчик изменения описания продукта в поле ввода
const handleOverviewChange = (event) => {
    setOverview(event.target.value);
};

// Обработчик изменения цены продукта в поле ввода
const handlePriceChange = (event) => {
    setPrice(event.target.value);
};

// Обработчик изменения доступности продукта в поле ввода
const handleProductAvabilityChange = (event) => {
    setProductAvability(event.target.checked);
};

    const handleSubmit = async (event) => {
        event.preventDefault();
        console.log('Id продукта:', productId);
        console.log('Путь изображения продукта:', productImage.name); // Выводим имя выбранного файла
        console.log('Название продукта:', productTitle);
        console.log('Добавьте картинку:', productImages);

        // Создаем объект FormData для отправки данных формы, включая файл
        const formData = new FormData();
        formData.append('id', productId);
        formData.append('image', productImage);
        formData.append('title', productTitle);

        try {
            const response = await fetch('http://localhost:3001/admin', {
                method: 'POST',
                body: formData // Отправляем данные в формате FormData
            });
            const data = await response.json();
            console.log('Ответ от сервера:', data);
        } catch (error) {
            console.error('Ошибка при отправке данных на сервер:', error);
        }
    };

    return (
        <div className={pageAdmin.container}>
            <h1>Страница администратора магазина</h1>
            <h3>Редактирование карточки товара</h3>
            <form onSubmit={handleSubmit}>
                <input 
                    type="text" 
                    placeholder="Введите Id продукта" 
                    value={productId} 
                    onChange={handleProductIdChange} 
                /><br/>
                <input 
                    type="file" 
                    placeholder="Добавьте картинку" 
                    onChange={handleProductImageChange} 
                /><br/>
                <input 
                    type="text" 
                    placeholder="Введите название изображения" 
                    value={productImage.name} // Отображаем имя выбранного файла
                    disabled // Запрещаем изменение значения
                /><br/>
                <input 
                    type="text" 
                    placeholder="Введите название продукта" 
                    value={productTitle} 
                    onChange={handleProductTitleChange} 
                /><br/>
                <button type="submit">Добавить продукт</button>
            </form>   
        </div>
    );
}

export default PageAdmin;
