import React, { useState } from 'react';
import pageAdmin from './PageAdmin.module.css';

const PageAdmin = () => {
    const [productId, setProductName] = useState('');
    const [productTitle, setProductPrice] = useState('');

    // Обработчик изменения значения в поле ввода названия продукта
    const handleProductNameChange = (event) => {
        setProductId(event.target.value);//функция для обновления значения
    };

    // Обработчик изменения значения в поле ввода цены продукта
    const handleProductPriceChange = (event) => {
        setProductTitle(event.target.value);//функция для обновления значения
    };

    // Обработчик отправки формы
    const handleSubmit = (event) => {
        event.preventDefault();
        console.log('Название продукта:', productId);
        console.log('Цена продукта:', productTitle);
        // Добавьте здесь логику для отправки данных на сервер
    };

    return (
        <div className={pageAdmin.container}>
            <form onSubmit={handleSubmit}>
                {/* Поле ввода для названия продукта */}
                <input 
                    type="text" 
                    placeholder="Введите название продукта" 
                    value={productName} 
                    onChange={handleProductNameChange} 
                />

                {/* Поле ввода для цены продукта */}
                <input 
                    type="number" 
                    placeholder="Введите цену продукта" 
                    value={productPrice} 
                    onChange={handleProductPriceChange} 
                />

                {/* Кнопка отправки формы */}
                <button type="submit">Добавить продукт</button>
            </form>
        </div>
    );
}

export default PageAdmin;
