import React, { useState, useEffect } from 'react';//импортирование методов хуков
import ProductCard from './Components/ProductCard/ProductCard';
function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    // Отправляем GET запрос к серверу на Express
    fetch('http://localhost:3001/product')
      .then(response => response.json())
      .then(data => {
        // Обновляем состояние данных в компоненте React
        setData(data);

      })
      .catch(error => {
        console.error('Ошибка получения данных из сервера:', error);
      });
  }, []);
  console.log(data); // Выводим данные в консоль
  return (
    <div>
      <h1>Данные из Express:</h1>
      <ul>
        {/* Отображаем полученные данные */}
        {data.map(item => (
          <p key={item.id}>{item.img}{item.titleProduct}{item.overview}{item.productAvability}</p>

        ))}
      </ul>
      <ProductCard name={item.id} />
    </div>
  );
}

export default App;
