import React, { useState } from 'react';
import pageAdmin from './PageAdmin.module.css';

const PageAdmin = () => {
    const [productId, setProductId] = useState('');//создается состояние productId
    
    
    const handleProductIdChange = (event) => {// функция предназначенная для обновления состояния productId в React при изменении значения поля ввода
        setProductId(event.target.value);
    };


    const handleSubmit = async (event) => {//вызывается при отправке формы
        event.preventDefault();



    }
    
return (
    <div>
        <div className={pageAdmin.container}>
            <h1>Страница администратора магазина</h1>
            <h3>Редактирование карточки товара</h3>
            <form onSubmit={handleSubmit}>
                <input 
                    type="text" 
                    placeholder="Введите Id продукта" 
                    value={productId} 
                    onChange={handleProductIdChange} 
                /><br/>
                <button type="submit">Добавить продукт</button>
            </form> 
        </div>
    </div>

    );
};

export default PageAdmin;



