import React, { useState } from 'react';
import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption,
} from 'reactstrap';
/*import carusel from './Carusel/Carusel.module.css'*/



const items = [
  {
    src: '../Images/cake.jpg',
    altText: 'Сладкий рулет',
    caption: 'Меренговый рулет',
    key: 1,
  },
  {
    src: '../Images/background.png',
    altText: 'Slide 2',
    caption: 'Slide 3',
    key: 2,
  },
  {
    src: '../Images/bomb.png',
    altText: 'Slide 3',
    caption: 'Slide 3',
    key: 3,
  },
];

function Carusel(args) {
  const [activeIndex, setActiveIndex] = useState(0);
  const [animating, setAnimating] = useState(false);

  const next = () => {
    if (animating) return;
    const nextIndex = activeIndex === items.length - 1 ? 0 : activeIndex + 1;
    setActiveIndex(nextIndex);
  };

  const previous = () => {
    if (animating) return;
    const nextIndex = activeIndex === 0 ? items.length - 1 : activeIndex - 1;
    setActiveIndex(nextIndex);
  };

  const goToIndex = (newIndex) => {
    if (animating) return;
    setActiveIndex(newIndex);
  };
  const caruselItemStyle = {
    width: '50%', // ширина
    height: '470px', //  высота
  };
  
  const slides = items.map((item) => {
    return (
      <CarouselItem style={caruselItemStyle}
      
        onExiting={() => setAnimating(true)}
        onExited={() => setAnimating(false)}
        key={item.src}
      >
        <img src={item.src} alt={item.altText} style={caruselItemStyle}/>
        <CarouselCaption
        
          
          captionHeader={item.caption}
        />
      </CarouselItem>
    );
  });

  return (
    <Carousel
      activeIndex={activeIndex}
      next={next}
      previous={previous}
      {...args}
    >
      <CarouselIndicators
        items={items}
        activeIndex={activeIndex}
        onClickHandler={goToIndex}
      />
      {slides}
      <CarouselControl
        direction="prev"
        directionText="Previous"
        onClickHandler={previous}
      />
      <CarouselControl
        direction="next"
        directionText="Next"
        onClickHandler={next}
      />
    </Carousel>
  );
}

export default Carusel;
