import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import ProductCard from './Components/ProductCard/ProductCard';
import Display from './Components/Display/Display';

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/product')
      .then(response => response.json())
      .then(data => {
        setData(data);
      })
      .catch(error => {
        console.error('Ошибка получения данных из сервера:', error);
      });
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home data={data} />} />
        <Route path="/admin" element={<Admin data={data} />} />
      </Routes>
    </Router>
  );//Routes используются вместо Switch в этой версии для настройки маршрутизации
}

function Home({ data }) {
  return (
    <div>
      <Display />
      <ProductCard data={data} />
    </div>
  );
}

function Admin({ data }) {
  return (
    <div>
      <Display />
      {/* Вывод компонентов для страницы продуктов */}
      {/* Например, здесь можно отобразить компонент ProductList */}
    </div>
  );
}

export default App;
