import React from 'react';
import productCard from './productCard.module.css';
import cupcakes from '../../Images/cupcakes.png';


function ProductCard(props) {
  return (
    <div>
      <h2>Карточка товара</h2>
      {/* Вывод данных */}
      
        {props.data.map(item => (
          <div className={productCard.background}>
              <div className={productCard.image} alt={item.titleProduct}>{/*картинка товара productPicture*/}
                <img src={cupcakes} alt={item.titleProduct}/>{/*сама картинка*/}
              </div>
              <div className={productCard.overview}>{/*обертка описания товара*/}
                  <div className={productCard.titleProduct}>Название товара</div>{/*название товара creme*/}
                  <div className={productCard.overview}>Нежный крем любого цвета<br />на выбор, вафельная основа</div>{/*описание товара*/}
                  <div className={productCard.price}>150р/шт.</div>{/*цена товара*/}
                  <div className={productCard.productAvability}>Заказать </div>{/*наличие заказа*/}
                  
                  
              </div>
            </div>
    
        ))}
      
      </div>
      
  );
}

export default ProductCard;
