import React, { useState, useEffect } from 'react';//импортирование методов хуков
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ProductCard from './Components/ProductCard/ProductCard';//Импортирование компоненты ProductCard
import Display from './Components/Display/Display';//Импортирование компоненты Display
import Home from './Components/Home';
import About from './Components/About';
import Contact from './Components/Contact';
import NotFound from '.Ccomponents/NotFound'

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    // Отправляется GET запрос к серверу на Express
    fetch('http://localhost:3001/product')
      .then(response => response.json())
      .then(data => {
        // Обновляем состояние данных в компоненте React
        setData(data);

      })
      .catch(error => {
        console.error('Ошибка получения данных из сервера:', error);
      });//проверка на ошибки
  }, []);
  console.log(data); // Выводим данные в консоль для контроля поступления данных с сервера
  return (
    <div>
       <Display />
      <ProductCard data={data} />
    </div>
  );//data принимаемая с сервера отдается в другие компоненты
  <Router>
      <div>
        <Switch>
          <Route exact path="/" component={Home} />
          <Route path="/about" component={About} />
          <Route path="/contact" component={Contact} />
          <Route component={NotFound} />
        </Switch>
      </div>
    </Router>
}



export default App;
