import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import ProductCard from './Components/ProductCard/ProductCard';
import Display from './Components/Display/Display';
import PageAdmin from './Components/PageAdmin/PageAdmin';


function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001')
      .then(response => response.json())
      .then(data => {
        setData(data);
      })
      .catch(error => {
        console.error('Ошибка получения данных из сервера:', error);
      });
  }, []);


  const handleAdminSubmit = () => {
      fetch('http://localhost:3001/admin', {//отправка запроса со станицы адмиистратора на сервер
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
  },
  body: JSON.stringify({ titleProduct: 'новых данных нет' }),
    })
  .then(response => response.json())
  .then(data => {
    console.log('Успешно обновлено:', data);
  })
  .catch(error => {
    console.error('Ошибка при отправке данных на сервер:', error);
  });
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home data={data} />} />
        <Route path="/admin" element={<Admin onSubmit={handleAdminSubmit} />} />
      </Routes>
    </Router>
  );//Routes используются вместо Switch в этой версии для настройки маршрутизации
}

function Home({ data }) {
  return (
    <div>
      <Display />

      <ProductCard data={data} />
    </div>
  );//ProductCard отображение карточки товара
}

function Admin({ onSubmit }) {
  return (
    <div>
      <PageAdmin onSubmit={onSubmit} />
    </div>
  );
}

export default App;
