import React, { useState } from 'react';
import pageAdminStyles from './PageAdmin.module.css';

const PageAdmin = () => {
    const [titleProduct, setTitleProduct] = useState('');
    const [overview, setOverview] = useState('');

    const handleProductTitleChange = (event) => {
        setTitleProduct(event.target.value);
    };

    const handleOverview = (event) => {
        setOverview(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        fetch('http://localhost:3001/admin', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ titleProduct, overview }),
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Ошибка при отправке запроса');
            }
            return response.json();
        })
        .then(data => {
            console.log('Успешно обновлено:', data);
        })
        .catch(error => {
            console.error('Ошибка при отправке данных на сервер:', error);
        });
    };

    return (
        <div>
            <div className={pageAdminStyles.container}>
                <h1>Страница администратора магазина</h1>
                <h3>Редактирование карточки товара</h3>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        placeholder="Введите название продукта"
                        value={titleProduct}
                        onChange={handleProductTitleChange}
                    /><br/>
                    <input
                        type="text"
                        placeholder="Введите описание продукта"
                        value={overview}
                        onChange={handleProductOverview}
                    /><br/>
                    <button type="submit">Добавить продукт</button>
                </form>
            </div>
        </div>
    );
};

export default PageAdmin;




