import React, { useState } from 'react';
import pageAdminStyles from './PageAdmin.module.css';

const PageAdmin = ({ onSubmit }) => {
    const [titleProduct, setProductTitle] = useState('');//создается состояние productId хранит текущее значение поля ввода
    
    
    const handleProductTitleChange = (event) => {// функция предназначенная для обновления состояния productId в React при изменении значения поля ввода
        setProductTitle(event.target.value);
    };


    const handleSubmit = (event) => {  //вызывается при отправке формы
        event.preventDefault();
        onSubmit({ titleProduct });
    };
    
        

    
       

return (
    <div>
        <div className={pageAdminStyles.container}>
            <h1>Страница администратора магазина</h1>
            <h3>Редактирование карточки товара</h3>
            <form onSubmit={handleSubmit}>
                <input 
                    type="text" 
                    placeholder="Введите название продукта" 
                    value={titleProduct} 
                    onChange={handleProductTitleChange} 
                /><br/>
                <button type="submit">Добавить продукт</button>
            </form> 
        </div>
    </div>

    );
    
};

export default PageAdmin;



