import React from 'react';
import productCard from './productCard.module.css';

function ProductCard(props) {
  return (
    <div className={productCard.container}>
      {props.data.map((item) => (
        <div key={item.id} className={productCard.conta}>
          <div className={productCard.image}>
            {/* Картинка товара */}
            <img className={productCard.imageProduct} src={item.image} alt={item.titleProduct} />
          </div>
          <div className={productCard.overview}>
            {/* Обертка описания товара */}
            <div className={productCard.titleProduct}>{item.titleProduct}</div>
            {/* Название товара */}
            <div className={productCard.description}>{item.overview}</div>
            {/* Описание товара */}
            <div className={productCard.price}>₽{item.price}</div>
            {/* Цена товара */}
            <button className={productCard.productAvability}>В наличии:{item.productAvability} </button>
            {/* Наличие заказа */}
          </div>
        </div>
      ))}
    </div>
  );
}

export default ProductCard;
